mapper.select:
SELECT employee_id, name, gender, birth_date, hire_date, position, department, phone, email, address,
education, work_experience, skills, salary
FROM employee
WHERE 1=1

    AND employee_id = #{employeeId}


    AND name = #{name}


    AND department = #{department}

mapper.insert:
INSERT INTO employee (
name, gender, birth_date, hire_date, position, department,
phone, email, address, education, work_experience, skills, salary
) VALUES (
#{name}, #{gender, typeHandler=org.example.oasystem.model.GenderEnumTypeHandler},
#{birthDate}, #{hireDate}, #{position}, #{department},
#{phone}, #{email}, #{address},
#{education}, #{workExperience}, #{skills}, #{salary}
)
