mapper.select:
SELECT id, name, gender, birth_date, hire_date, position, department, phone, email, address,
status, created_at, updated_at
FROM employee
WHERE 1=1

    AND id = #{id}


    AND name = #{name}


    AND department = #{department}

mapper.insert:
INSERT INTO employee (
name, gender, birth_date, hire_date, position, department,
phone, email, address, status
) VALUES (
#{name}, #{gender, typeHandler=org.example.oasystem.model.GenderEnumTypeHandler},
#{birthDate}, #{hireDate}, #{position}, #{department},
#{phone}, #{email}, #{address}, #{status,typeHandler=org.example.oasystem.model.EmployeeStatusEnumTypeHandler}
)
