mapper.insert:
INSERT INTO leave_record (
employee_id, leave_type, leave_start_date, leave_end_date, reason, status
) VALUES (
#{employeeId}, #{leaveType,typeHandler=org.example.oasystem.model.LeaveTypeEnumTypeHandler}, #{leaveStartDate},
#{leaveEndDate}, #{reason}, #{status,typeHandler=org.example.oasystem.model.LeaveStatusEnumTypeHandler}
)

mapper.select:
SELECT id, employee_id, leave_type, leave_start_date, leave_end_date, reason, status
FROM leave_record
WHERE id = #{id}

SELECT id, employee_id, leave_type, leave_start_date, leave_end_date, reason, status
FROM leave_record
WHERE employee_id = #{employeeId}
